let BaseComponent      = require('./BaseComponent');
let map                = require('lodash/map');
let {WebHookConstants} =  require('../../constants');

class BaseActionContainer extends BaseComponent {

    setIcon(icon) {
        this.config.icon = icon;
        return this;
    }

    setLabelPositionLeft() {
        this.config.labelPosition = 'left';
        return this;
    }

    setLabelPositionRight() {
        this.config.labelPosition = 'right';
        return this;
    }

    setIconColor(iconColor) {
        this.config.iconColor = iconColor;
        return this;
    }

    setWebhook(url, method = WebHookConstants.GET_METHOD, header, body) {
        /** remove header as it is replaced via headers**/
        this.config.webhook = {
            url, method, header, body, headers: header
        };
        return this;
    }
}
module.exports = BaseActionContainer;
