// import uuid from 'uuid';
let uuid = require('uuid');

class BaseComponent {

    constructor(params) {
        this.config = {};
    }

    setClassName(className) {
        this.config.className = className;
        return this;
    }

    setTitle(title) {
        this.config.title = title;
        return this;
    }

    setId(id) {
        this.config.id = id || uuid();
        return this;
    }

    setDisabled(disabled) {
        this.config.disabled = disabled;
        return this;
    }

    setName(name) {
        this.config.name = name;
        return this;
    }

    setVisibility(qry_obj) {
        this.config.visibility = qry_obj;
        return this;
    }

    render() {
        return this.config;
    }

}

module.exports = BaseComponent;
